style.css:
@import url('https://fonts.googleapis.com/css?family=Source+Sans+Pro:400,700,900&display=swap');
:host {
  top: 0;
  right: 0;
  bottom: 0;
  left: 0;
}
:host .container {
  margin: auto;
}
.container {
  font-family: Source Sans Pro, "Helvetica Neue", Helvetica, Arial, sans-serif;
  box-shadow: 0 11px 32px -5px rgba(54, 61, 77, 0.15);
}
@media (min-width: 960px) {
  .container {
    box-shadow: 0 10px 24px 0 rgba(54, 61, 77, 0.15);
  }
}
.title {
  font-weight: 900;
  font-size: 24px;
  line-height: 1.3333;
  margin-bottom: 24px;
}
@media (min-width: 960px) {
  .title {
    font-size: 28px;
    line-height: 1.2857;
  }
}
.description {
  font-size: 16px;
  margin-bottom: 24px;
}
.no-fields .form {
  margin-top: 8px;
}
.fields {
  margin-bottom: 32px;
}
.field + .field {
  margin: 24px 0 0 0;
}
.field-title {
  margin: 0 0 12px 0;
}
.radio .field-title,
.checkbox .field-title {
  margin: 0 0 16px 0;
}
.buttons {
  margin: -6px;
}
.buttons .button {
  margin: 6px;
  border-radius: 6px;
}
.button.normal,
.button.normal.icon {
  font-weight: normal;
  background-color: transparent;
  border-color: rgba(38, 44, 49, 0.1);
}
.button.normal:hover,
.button.normal.icon:hover,
.button.normal:focus,
.button.normal.icon:focus {
  border-color: #262c31;
}
.button.secondary,
.button.secondary.icon {
  padding-left: 0;
  padding-right: 0;
  color: rgba(38, 44, 49, 0.3);
  background-color: transparent;
  font-weight: normal;
}
.button.secondary:hover,
.button.secondary.icon:hover,
.button.secondary:focus,
.button.secondary.icon:focus {
  color: rgba(38, 44, 49, 0.7);
  background-color: transparent;
}
.input-text,
.input-textarea,
.input-select {
  border: none;
  border-bottom: solid 1px rgba(38, 44, 49, 0.1);
  border-radius: 0;
  background-color: transparent;
}
.input-text::placeholder,
.input-textarea::placeholder,
.input-select::placeholder {
  color: rgba(38, 44, 49, 0.3);
}
.input-text:hover,
.input-textarea:hover,
.input-select:hover,
.input-text:focus,
.input-textarea:focus,
.input-select:focus {
  border-color: rgba(38, 44, 49, 0.7);
}
.input-text {
  padding: 0 0 11px 0;
}
.input-textarea {
  padding: 0;
}
.input-select select {
  padding: 0 24px 11px 0;
}
.input-select:after {
  top: 8px;
  transform: none;
  right: 0;
}
.input-check label {
  margin-bottom: 20px;
  border-radius: 4px;
}
.input-check label span {
  padding-left: 36px;
}
.input-check label span:before {
  width: 24px;
  height: 24px;
  top: 0;
}
.input-check label input[type='radio'] + span:before,
.input-check label input[type='checkbox'] + span:before {
  background-color: transparent;
  border: 1px solid rgba(38, 44, 49, 0.1);
}
.input-check label input[type='radio'] + span:after,
.input-check label input[type='checkbox'] + span:after {
  left: 4px;
}
.input-check label input[type='radio']:checked + span:after,
.input-check label input[type='checkbox']:checked + span:after {
  left: 4px;
}
.input-check label:hover input[type='radio'] + span:before,
.input-check label:focus input[type='radio'] + span:before,
.input-check label:hover input[type='checkbox'] + span:before,
.input-check label:focus input[type='checkbox'] + span:before {
  border-color: #262c31;
}
.input-rating label {
  width: 40px;
  height: 32px;
}
.note {
  margin: 32px 0 0 0;
  color: rgba(38, 44, 49, 0.3);
}
@keyframes scale-in-center {
  0% {
    transform: scale(0.5);
    opacity: 0;
  }
  100% {
    transform: scale(1);
    opacity: 1;
  }
}
:host(.enter) .container {
  animation: scale-in-center 0.4s cubic-bezier(0.39, 0.575, 0.565, 1) both;
}
:host(.leave) .container {
  animation: scale-in-center 0.4s cubic-bezier(0.39, 0.575, 0.565, 1) both reverse;
}
@keyframes shake {
  100% {
    transform: none;
  }
  0%,
  99% {
    transform: translate(0, 0);
  }
  20% {
    transform: translateX(-10px);
  }
  60% {
    transform: translateX(-5px);
  }
  40% {
    transform: translateX(10px);
  }
  80% {
    transform: translateX(5px);
  }
}
.form.submit .invalid {
  animation: shake 600ms;
}
@keyframes sent-backdrop {
  0%,
  100% {
    opacity: 0;
    background-color: rgba(255, 255, 255, 0);
  }
  50%,
  75% {
    opacity: 0.6;
    background-color: #ffffff;
  }
}
@keyframes success-icon-scale {
  0% {
    opacity: 0;
    transform: translate(-50%, -50%) scale(0.8);
  }
  50% {
    opacity: 1;
    transform: translate(-50%, -50%) scale(1);
  }
  75% {
    opacity: 1;
    transform: translate(-50%, -50%) scale(1);
  }
  100% {
    opacity: 0;
    transform: translate(-50%, -50%) scale(0.2);
  }
}
@keyframes success-icon-show-path {
  0% {
    opacity: 0;
  }
  50% {
    opacity: 0;
  }
  100% {
    opacity: 1;
  }
}
@keyframes success-icon-draw-circle {
  0% {
    opacity: 1;
    stroke-dashoffset: 150.72;
  }
  25% {
    opacity: 1;
    stroke-dashoffset: 75.36;
  }
  50% {
    opacity: 1;
    stroke-dashoffset: 0;
  }
  100% {
    opacity: 0;
    stroke-dashoffset: 0;
  }
}
@keyframes success-icon-draw-check {
  0% {
    opacity: 1;
    stroke-dashoffset: 36;
  }
  25% {
    opacity: 1;
    stroke-dashoffset: 18;
  }
  50% {
    opacity: 1;
    stroke-dashoffset: 0;
  }
  100% {
    opacity: 0;
    stroke-dashoffset: 0;
  }
}
:host(.form-sending) .content:before {
  content: '';
  display: block;
  position: absolute;
  top: 0;
  right: 0;
  bottom: 0;
  left: 0;
  z-index: 1;
  animation: sent-backdrop 2s both;
}
:host(.form-sending) .content .success-icon {
  display: block;
  width: 100%;
  height: 100%;
  animation: success-icon-scale 1.8s linear both;
}
:host(.form-sending) .content .success-icon path {
  animation: success-icon-show-path 1.2s linear both;
}
:host(.form-sending) .content .success-icon circle {
  animation: success-icon-draw-circle 1.2s linear both;
}
:host(.form-sending) .content .success-icon polyline {
  animation: success-icon-draw-check 1.2s linear both;
}
